    const loadSlide = (index) => {
        document.querySelector('.slideshow__img').src = images + images[index].image
        document.querySelector('.dot_selected').classList.remove('dot_selected')
        document.querySelector('[data-slide="' + index + '"]').classList.add('dot_selected')
    }
/*
    const arrowLeft = () => {
        console.log("clic gauche")
        let selectedDotIndex = parseInt(document.querySelector('.dot_selected').dataset.image)
        loadSlide(selectedDotIndex === 0 ? images.length - 1 : selectedDotIndex - 1)
    }

    const arrowRight = () => {
        console.log("clic droit")
        let selectedDotIndex = parseInt(document.querySelector('.dot_selected').dataset.image)
        loadSlide(selectedDotIndex === images.length - 1 ? 0 : selectedDotIndex + 1)
    }
*/
    const genSlidersDot = (image, index) => {
        console.log(index)
        return (
            <span key={index} className={"dot" + (index === 0 ? ' dot_selected' : '')} data-image={index} ></span>
        )

        /*
        var dot = document.createElement('span')
        dot.className = 'dot' + (index === 0 ? ' dot_selected' : '')
        dot.dataset.slide = index
        document.getElementById('dots').append(dot)*/
    }

    const genSlidersDots = () => {
        console.log('okiiii')
        images.map((image, index) => genSlidersDot(image, index))
    }



    return (
        <div className="slideshow">
            {images.map((image, index) => <img className="slideshow__img" key={index} src={image} alt="logement"/>)}
            {genSlidersDots()}
            <i className={images.length === 0 ? "fa fa-chevron-left hide" : "fa fa-chevron-left" }></i>
            <i className={images.length === 0 ? "fa fa-chevron-right hide" : "fa fa-chevron-right" }></i>
            <div className="dots" id="dots"></div>
        </div>
    )
};

export default Slideshow
