import React, { useState } from "react";

const Collapsible = (prop) => {
    const [isOpen, setIsOpen] = useState(false);

    function handleClick() {
        setIsOpen(!isOpen)
    }

    console.log(typeof(prop.description))

    function isObject(descriptions) {
        typeof(descriptions) === 'object' ?
            console.log('ok') : console.log('KO')
            /*{descriptions.map(description => <div>
                {description}
            </div>)}
        : descriptions*/
    }

    return (
        <li key={prop.id} className="collapsible">
            <p className="collapsible__title">
                {prop.name}
                <i className={isOpen ? "fa fa-chevron-up animation" : "fa fa-chevron-up animation-reverse"} onClick={handleClick}></i>
            </p>
            <div className="collapsible__content">
                <p className={isOpen ? "collapsible__content--show" : "collapsible__content--hide"}>isObject(prop.description)</p>
            </div>
        </li>
    )

}

export default Collapsible;
