import {useParams} from 'react-router-dom'; 
import Collapsible from '../../components/Collapse';
import Slideshow from '../../components/Slideshow';


const Logement = () => {
    const logement = {
        "id": "c67ab8a7",
        "title": "Appartement cosy",
        "cover": "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-1.jpg",
        "pictures": [
            "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-1.jpg",
            "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-2.jpg",
            "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-3.jpg",
            "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-4.jpg",
            "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-5.jpg"
        ],
        "description": "Votre maison loin de chez vous. Que vous veniez de l'autre bout du monde, ou juste de quelques stations de RER, vous vous sentirez chez vous dans notre appartement.",
        "host": {
            "name": "Nathalie Jean",
            "picture": "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/profile-picture-12.jpg"
        },
        "rating": "5",
        "location": "Ile de France - Paris 17e",
        "equipments": [
            "Équipements de base",
            "Micro-Ondes",
            "Douche italienne",
            "Frigo",
            "WIFI"
        ],
        "tags": [
            "Batignolle",
            "Montmartre"
        ]
    }

    const {logementId} = useParams();
    return(
        <main key={logementId} className='logement'>
            {Slideshow(logement.pictures[0])}
                <div className='logement__title'>{logement.title}</div>
                <div className='logement__location'>{logement.location}</div>
                <div className='tag_and_ratings'>
                    <div className='tags'>
                        {(logement.tags).map(tag => {
                            return (
                                <span className='tags__tag'>
                                <i class="fa-xs fa-solid fa-star" aria-hidden="true"></i>
                                <i class="fa-xs fa-solid fa-star" aria-hidden="true"></i>
                                <i class="fa-xs fa-solid fa-star" aria-hidden="true"></i>
                                <i class="fa-xs fa-solid fa-star" aria-hidden="true"></i>
                                <i class="fa-xs fa-solid fa-star neutralStar" aria-hidden="true"></i>
                                    {tag}
                                </span>
                            )
                        })}
                    </div>
                </div>
                <div className='host-and-ratings'>
                    <div className='host-and-ratings__host'>
                        <span className='host-and-ratings__host--name'>{logement.host.name}</span>
                        <img className='host-and-ratings__host--img' src= {logement.host.picture} alt= {logement.host.name} />
                    </div>
                    <div className='host-and-ratings__ratings'>{logement.rating}</div>
                </div>
                {Collapsible(logement.description)}
                {Collapsible(logement.equipments)}
        </main>
    )
}

export default Logement
