function Slideshow(images) {

    function handleClickArrowLeft() {
        console.log('tu as cliqué sur la fleche gauche')
    }

    function handleClickArrowRight() {
        console.log('tu as cliqué sur la fleche droite')
    }

    return (
        <div className="slideshow">
            {images.map(image => <img className="slideshow__img" key={image.toString()} src={image} alt="logement"/>)}
            <i className={images.length === 0 ? "fa fa-chevron-left hide" : "fa fa-chevron-left" } onClick={handleClickArrowLeft}></i>
            <i className={images.length === 0 ? "fa fa-chevron-right hide" : "fa fa-chevron-right" } onClick={handleClickArrowRight}></i>
        </div>
    )
};

export default Slideshow
