        /*
        let selectedDotIndex = parseInt(document.querySelector('.dot_selected').dataset.slide)
        loadSlide(selectedDotIndex === images.length - 1 ? 0 : selectedDotIndex + 1)*/
    }

    const genSlidersDot = (image, index) => {
        console.log('ok')
        var dot = document.createElement('span')
        dot.className = 'dot' + (index === 0 ? ' dot_selected' : '')
        dot.dataset.slide = index
        document.getElementById('dots').append(dot)
    }

    const genSlidersDots = () => {
        images.map(image => genSlidersDot)
    }


    return (
        <div className="slideshow">
            {images.map(image => <img className="slideshow__img" key={image.toString()} src={image} alt="logement"/>)}
            <i className={images.length === 0 ? "fa fa-chevron-left hide" : "fa fa-chevron-left" } onClick={arrowLeft}></i>
            <i className={images.length === 0 ? "fa fa-chevron-right hide" : "fa fa-chevron-right" } onClick={arrowRight}></i>
            {genSlidersDots()}
        </div>
    )
};

export default Slideshow
