import React, { useState } from 'react';

function Slideshow(images) {

    const [index, setIndex] = useState(0); 
    const length = images.length;

    const loadSlide = (index) => {
        console.log(index, images[index])
        return (
            <img className="slideshow__img" src={images[index]} alt="logement"/>
        )
    }

    const arrowLeft = () => {
        const newIndex = index - 1;
        setIndex(newIndex < 0 ? length - 1 : newIndex);
        loadSlide(newIndex)

    }

    const arrowRight = () => {
        const newIndex = index + 1;
        setIndex(newIndex > length -1 ? 0 : newIndex);
        loadSlide(newIndex)
    };

    return (
        <div className="slideshow">
            {images.map((image, index) => <img className="slideshow__img" key={index} src={image} alt="logement"/>)}
            <i className={images.length <= 1 ? "fa fa-chevron-left hide" : "fa fa-chevron-left" } onClick={arrowLeft}></i>
            <i className={images.length <= 1? "fa fa-chevron-right hide" : "fa fa-chevron-right" } onClick={arrowRight}></i>
            <div className="dots">
                {images.map((image, index) => <span key={index} className={images.length >= 1 ? ("dot" + (index === 0 ? ' dot_selected' : '')) : "hide"}></span>)}
            </div>
        </div>
    )
};

export default Slideshow
