import { Link } from 'react-router-dom';
import Logement from '../../pages/Logement';
import { useState } from 'react';

const Card = (logement) => {

    const [data, setData] = useState('');

    const parentToChild = () => {
        setData("Ce sont des données passées du composant parent au composant enfant.");
    }

    return (
        <div className="card">
            <Logement parentToChild={data}/>
            <Link key={logement.id} to={`logement/${logement.id}`} onClick={() => parentToChild()}>
                <img className="card__img" src={logement.cover} alt={logement.title}/>
                <p className="card__text">{logement.title}</p> 
            </Link>
        </div>
    )
}

export default Card
