import { Link } from 'react-router-dom';
//import Logement from '../../pages/Logement';

function handleClick(logement) {
    console.log(logement)
}

const Card = (logement) => {
    return (
        <Link key={logement.id} className="card" to={`logement/${logement.id}`} onClick={() => handleClick(logement)}>
            <img className="card__img" src={logement.cover} alt={logement.title}/>
            <p className="card__text">{logement.title}</p> 
        </Link>
    )
}

export default Card
