import React, { useState } from 'react';

function Slideshow(images) {
/*
    const loadSlide = (index) => {
        document.querySelector('.slideshow__img').src = images + images[index].image
        document.querySelector('.dot_selected').classList.remove('dot_selected')
        document.querySelector('[data-slide="' + index + '"]').classList.add('dot_selected')
    }
*/
{
    const [index, setIndex] = useState(0); 
    const length = images.length;


    const arrowLeft = () => {
        console.log("clic gauche")
        const newIndex = index - 1;
        setIndex(newIndex < 0 ? length - 1 : newIndex);
    }

    const arrowRight = () => {
        console.log("clic droit")
        const newIndex = index + 1;
        setIndex(newIndex >= length ? 0 : newIndex);
  };
    }

    return (
        <div className="slideshow">
            {images.map((image, index) => <img className="slideshow__img" key={index} src={image} data-image={index} alt="logement"/>)}
            <i className={images.length === 0 ? "fa fa-chevron-left hide" : "fa fa-chevron-left" } onClick={arrowLeft}></i>
            <i className={images.length === 0 ? "fa fa-chevron-right hide" : "fa fa-chevron-right" } onClick={arrowRight}></i>
            <div className="dots">
                {images.map((image, index) => <span key={index} className={"dot" + (index === 0 ? ' dot_selected' : '')}></span>)}
            </div>
        </div>
    )
};

export default Slideshow
